// script.min.js – alap JS helper funkciók

// Accordion működtetése (ha használod Bootstrap accordiont)
document.addEventListener("DOMContentLoaded", function () {
  const accordions = document.querySelectorAll('.accordion');
  accordions.forEach((acc) => {
    acc.addEventListener('click', function () {
      this.classList.toggle('active');
      const panel = this.nextElementSibling;
      if (panel.style.maxHeight) {
        panel.style.maxHeight = null;
      } else {
        panel.style.maxHeight = panel.scrollHeight + "px";
      }
    });
  });
});

// Swiper inicializálás (ha van ilyen class)
try {
  if (typeof Swiper !== "undefined") {
    const swiper = new Swiper(".swiper", {
      loop: true,
      pagination: {
        el: ".swiper-pagination",
        clickable: true,
      },
      navigation: {
        nextEl: ".swiper-button-next",
        prevEl: ".swiper-button-prev",
      },
    });
  }
} catch (e) {
  console.warn("Swiper nem aktív vagy hiányzik:", e);
}

// Iframe auto-resize helper (pl. Google Calendarhez, ha kell)
window.addEventListener("message", function (e) {
  if (e.data && e.data.type === "resize-iframe") {
    const iframe = document.querySelector("iframe[data-resize-id='" + e.data.id + "']");
    if (iframe) iframe.style.height = e.data.height + "px";
  }
});
